package com.cs.ioc;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;/** * @author: demopoo * @Date: Created in 下午1:40 2018/3/19 * @Des: * @Modifyed By: */public abstract class AbstractBeanFactory implements BeanFactory {    private Map<String,BeanDefinition> map = new ConcurrentHashMap<>();    @Override    public Object getBean(String beanName) {        return map.get(beanName);    }    @Override    public void registerBeanDefinition(String beanName, BeanDefinition beanDefinition) {        this.createBean(beanDefinition);        map.put(beanName,beanDefinition);    }    public abstract BeanDefinition createBean(BeanDefinition beanDefinition);}