package com.cs.ioc;import java.lang.reflect.Field;import java.util.List;/** * @author: demopoo * @Date: Created in 下午1:44 2018/3/19 * @Des: * @Modifyed By: */public class AutowireCapableBeanFactory extends AbstractBeanFactory {    @Override    public BeanDefinition createBean(BeanDefinition beanDefinition) {        if (beanDefinition != null){            try {                Object object = beanDefinition.getBeanClass().newInstance();                List<PropertyItem> list = beanDefinition.getPropertyItems().list;                for (PropertyItem propertyItem : list){                    Field field = object.getClass().getDeclaredField(propertyItem.getPropertyName());                    field.setAccessible(true);                    field.set(object,propertyItem.getPropertyObject());                }                beanDefinition.setBean(object);            } catch (InstantiationException e) {                e.printStackTrace();            } catch (IllegalAccessException e) {                e.printStackTrace();            } catch (NoSuchFieldException e) {                e.printStackTrace();            }        }        return beanDefinition;    }}