package com.cs.ioc;/** * @author: demopoo * @Date: Created in 下午1:36 2018/3/19 * @Des: * @Modifyed By: */public class BeanDefinition {    private Object bean;    public Class beanClass;    public String beanName;    public PropertyItems propertyItems;    public BeanDefinition(){    }    public PropertyItems getPropertyItems() {        return propertyItems;    }    public void setPropertyItems(PropertyItems propertyItems) {        this.propertyItems = propertyItems;    }    public Object getBean() {        return bean;    }    public void setBean(Object bean) {        this.bean = bean;    }    public Class getBeanClass() {        return beanClass;    }    public void setBeanClass(Class beanClass) {        this.beanClass = beanClass;    }    public String getBeanName() {        return beanName;    }    public void setBeanName(String beanName) {        try {            this.beanClass = Class.forName(beanName);        } catch (ClassNotFoundException e) {            e.printStackTrace();        }        this.beanName = beanName;    }}