package com.cs.ioc;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;/** * @author: demopoo * @Date: Created in 下午12:48 2018/3/19 * @Des: * @Modifyed By: */public class BeanFactory {    private Map<String,BeanDefinition> map = new ConcurrentHashMap<String, BeanDefinition>();    public void registerBeanDefinition(String beanName,BeanDefinition beanDefinition){        map.put(beanName,beanDefinition);    }    public BeanDefinition getBean(String beanName){        return map.get(beanName);    }}