package com.cs.ioc;/** * @author: demopoo * @Date: Created in 下午3:37 2018/3/22 * @Des:  该类主要用来处理bean之间的依赖关系 * @Modifyed By: */public class BeanRefrence {    private String beanName;    private Object bean;    public BeanRefrence(String beanName) {        this.beanName = beanName;    }    public String getBeanName() {        return beanName;    }    public void setBeanName(String beanName) {        this.beanName = beanName;    }    public Object getBean() {        return bean;    }    public void setBean(Object bean) {        this.bean = bean;    }    @Override    public String toString() {        return "BeanRefrence{" +                "beanName='" + beanName + '\'' +                ", bean=" + bean +                '}';    }}