package com.cs.ioc.context;import com.cs.ioc.AbstractBeanFactory;/** * @author: demopoo * @Date: Created in 下午4:35 2018/3/22 * @Des: * @Modifyed By: */public abstract class AbstractApplicationContext implements ApplicationContext {    public AbstractBeanFactory abstractBeanFactory;    public AbstractApplicationContext(AbstractBeanFactory abstractBeanFactory){        this.abstractBeanFactory = abstractBeanFactory;    }    @Override    public Object getBean(String beanName) {        return this.abstractBeanFactory.getBean(beanName);    }    public abstract void refresh();}