package com.cs.ioc.xml;import com.cs.ioc.BeanDefinition;import java.util.HashMap;import java.util.Map;import java.util.concurrent.ConcurrentHashMap;/** * @author: demopoo * @Date: Created in 下午10:05 2018/3/19 * @Des:  存储从xml中加载的bean，以便后来注入到IOC中 * @Modifyed By: */public abstract class AbstractBeanDefinitionReader implements BeanDefinitionReader {    public Map<String,BeanDefinition> map ;    public AbstractBeanDefinitionReader(){        this.map = new ConcurrentHashMap<>();    }}